import React from 'react'
import { Image, Dimensions, ScrollView, ViewPagerAndroidBase, View } from 'react-native'
import { connect } from 'react-redux'

type Props = {
  detail: any
}

const DishDetail = (props: Props) => {
  const [open, setOpen] = React.useState(true)
  return (
    <View>
    <View onTouchEndCapture={() => setOpen(!open)}>
    <ScrollView
      horizontal
      pagingEnabled
      showsHorizontalScrollIndicator={true}
      scrollEventThrottle={200}
      decelerationRate="fast"
      >
      {props.detail.Images.map((gallery: { image: string }) => <View
      key={gallery.image}
      style={{ 
        width: Dimensions.get('screen').width,
        height: Dimensions.get('screen').height / (open ? 2 : 4) }}>
        <Image
          source={{ uri: gallery.image }}
          style={{ height: Dimensions.get('screen').height / (open ? 2 : 4) }}
        />
      </View>)}
    </ScrollView>
    </View>
    </View>
  )
}

const mapStateToProps = (state: any) => {
  return {
    detail: state.dish.detail
  }
}

export default connect(mapStateToProps)(DishDetail)
