import React from 'react'
import { View, Text, TextInput, TouchableOpacity, Image } from 'react-native'
import { connect } from 'react-redux'
import { getPokemons } from '../redux/actions/pokeActions'
import styles from '../styles/styles'

const { button, buttonText, textInput, pad } = styles

type Props = {
  getPokemons: Function
  pokemons: Array<{
    name: string
    url: string
  }>
  navigation: {navigate: Function}
}

const Pokemons = (props: Props) => {

  const [pokemon, setPokemon] = React.useState('')

  React.useEffect(() => {
    props.getPokemons(20)
  }, [])

  return (
    <View style={{backgroundColor: 'lightgrey'}}>
      <TextInput
        style={textInput}
        placeholder='Enter Pokemon'
        value={pokemon}
        onChangeText={(text: string) => setPokemon(text)} />
      <TouchableOpacity
        style={button}
        disabled={!pokemon}
        onPress={() => props.getPokemons(pokemon)}>
          <Text style={buttonText}>
            SUBMIT
          </Text>
        </TouchableOpacity>
      <View style={{
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'space-around' }}>
        {props.pokemons.map(poke =>
        <View
          key={poke.name}
          style={{
            margin: 10,
            backgroundColor: 'white',
            alignItems: 'center',
            borderRadius: 5,
            padding: 5 }}>
          <Image
            resizeMode='contain'
            style={{width: '25vw', height: 100}}
            source={{ uri: poke.url }} />
          <Text>
            {poke.name}
          </Text>          
        </View>)}
    </View>
    </View>
  )
}

const mapStateToProps = (state: any) => {
  return {
    pokemons: state.poke.pokemons
  }
}

export default connect(mapStateToProps, { getPokemons })(Pokemons)
