import React from 'react'
import { View, ScrollView, Text, TextInput, Image, Dimensions } from 'react-native'
import { connect } from 'react-redux'
import { getPokemons } from '../redux/actions/pokeActions'
import { getDishes } from '../redux/actions/dishActions'

type Props = {
  getPokemons: Function
  getDishes: Function
  dishes: Array<any>
  navigation: { navigate: Function }
}

const Dishes = (props: Props) => {

  const [dish, setDish] = React.useState('')

  React.useEffect(() => {
    props.getDishes()
  }, [])

  return (
    <View>
      <TextInput
        style={{
          margin: 15,
          borderWidth: 1,
          borderColor: '#00a24a',
          borderRadius: 30,
          paddingHorizontal: 15,
          paddingVertical: 5,
          backgroundColor: 'white'
        }}
        placeholder='Search Dishes'
        value={dish}
        onChangeText={(text: string) => setDish(text)} />
      <ScrollView>
        <View style={{
          flex: 1,
          flexDirection: 'row',
          flexWrap: 'wrap',
          margin: 'auto',
          padding: 5
        }}>
          {props.dishes.map(dis =>
            dis.name.toLowerCase().includes(dish.toLowerCase()) &&
            <View
              key={dis.name}
              style={{
                padding: 10,
                alignItems: 'center',
                borderRadius: 5,
                width: '50%'
              }}>
              <View
                style={{
                  backgroundColor: 'white',
                  width: '100%',
                  height: 260
                }}>
                <Image
                  resizeMode='cover'
                  style={{
                    borderRadius: 5,
                    width: '100%',
                    height: 200
                  }}
                  source={{ uri: dis.Images[0].image }} />
                <Text
                  style={{
                    width: '100%',
                    padding: 10,
                    textAlign: 'center'
                  }}>
                  {dis.name}
                </Text>
              </View>
            </View>
          )}
        </View>
      </ScrollView>
    </View>
  )
}

const mapStateToProps = (state: any) => {
  return {
    pokemons: state.poke.pokemons,
    dishes: state.dish.dishes
  }
}

export default connect(mapStateToProps, { getPokemons, getDishes })(Dishes)
