import React from 'react'
import { View, ScrollView, Text, TextInput, TouchableOpacity, Image, Dimensions } from 'react-native'
import { connect } from 'react-redux'
import { getPokemons } from '../redux/actions/pokeActions'
import { getDishes } from '../redux/actions/dishActions'
import styles from '../styles/styles'

const { button, buttonText, textInput, pad } = styles

type Props = {
  getPokemons: Function
  getDishes: Function
  dishes: Array<any>
  navigation: { navigate: Function }
}

const Dishes = (props: Props) => {

  const [dish, setDish] = React.useState('')

  React.useEffect(() => {
    props.getDishes()
  }, [])

  return (
    <View>
      <TextInput
        style={textInput}
        placeholder='Enter Dishes'
        value={dish}
        onChangeText={(text: string) => setDish(text)} />
      <ScrollView>
        <View style={{
          flex: 1,
          flexDirection: 'row',
          flexWrap: 'wrap',
          justifyContent: 'center',
          margin: 'auto'
        }}>
          {props.dishes.map(dis =>
            <View
              key={dis.name}
              style={{
                margin: 5,
                backgroundColor: 'white',
                alignItems: 'center',
                borderRadius: 5,
                padding: 10
              }}>
              <Image
                resizeMode='contain'
                style={{ width: (Dimensions.get('screen').width / 4), height: Dimensions.get('screen').width / 4 }}
                source={{ uri: dis.Images[0].image }} />
              <Text>
                {dis.name}
              </Text>
            </View>)}
        </View>
      </ScrollView>
    </View>
  )
}

const mapStateToProps = (state: any) => {
  return {
    pokemons: state.poke.pokemons,
    dishes: state.dish.dishes
  }
}

export default connect(mapStateToProps, { getPokemons, getDishes })(Dishes)
