import React from 'react'
import { Image, Dimensions, ScrollView, View } from 'react-native'
import { connect } from 'react-redux'

type Props = {
  detail: any
}

const DishDetail = (props: Props) => {
  const [open, setOpen] = React.useState(true)
  return (
    <ScrollView
      horizontal
    >
      <View style={{ height: Dimensions.get('screen').height / (open ? 2 : 4) }}>
        <Image
          source={{ uri: props.detail.Images[0].image }}
          style={{ height: Dimensions.get('screen').height / (open ? 2 : 4) }}
        />
      </View>
      <View style={{ height: Dimensions.get('screen').height / (open ? 2 : 4) }}>
        <Image
          source={{ uri: props.detail.Images[0].image }}
          style={{ height: Dimensions.get('screen').height / (open ? 2 : 4) }}
        />
      </View>
      <View style={{ height: Dimensions.get('screen').height / (open ? 2 : 4) }}>
        <Image
          source={{ uri: props.detail.Images[0].image }}
          style={{ height: Dimensions.get('screen').height / (open ? 2 : 4) }}
        />
      </View>
    </ScrollView>
  )
}

const mapStateToProps = (state: any) => {
  return {
    detail: state.dish.detail
  }
}

export default connect(mapStateToProps)(DishDetail)
